import styled from 'styled-components'
import { NavLink } from 'react-router-dom'

export const MyBalanceBox = styled.div`
  width: 100%;
  border-radius: 0.5625rem;
  box-shadow: 0.4375rem 0.125rem 1.625rem 0 rgba(0, 0, 0, 0.06);
  background-color: transparent;
  border: 3px solid rgba(159, 164, 252, 0.50);
  padding: 1rem 2.5rem;
  margin-bottom: 20px;
  overflow:auto;
  @media screen and (max-width: 960px) {
    padding: 1rem 1rem;
  }
`

export const Flex = styled.div`
  ${({ theme }) => theme.flexC};
`

export const TitleAndSearchBox = styled.div`
  ${({ theme }) => theme.flexBC};
  margin-bottom: 1.5625rem;
  font-family: 'Montserrat', sans-serif;
  h3 {
    font-size: 1rem;
    font-weight: bold;
    line-height: 1.5;
    color: ${({ theme }) => theme.textColorBold};
    margin: 0 1.25rem 0 0;
    white-space: nowrap;
  }
`
export const SearchBox = styled.div`
  width: 100%;
  max-width: 296px;
  height: 2.5rem;

  border-radius: 0.5625rem;
  box-shadow: 0 0.125rem 0.25rem 0 rgba(0, 0, 0, 0.04);
  border: solid 0.0625rem rgba(0, 0, 0, 0.1);
  padding-left: 2.5rem;
  position: relative;

  .icon {
    ${({ theme }) => theme.flexC};
    width: 2.5rem;
    height: 2.5rem;
    position: absolute;
    top: 0;
    left: 0;
    cursor: pointer;
  }
`

export const SearchInput = styled.input`
  width: 100%;
  height: 100%;
  color: ${({ theme }) => theme.textColorBold};
  position: relative;
  font-weight: 500;
  outline: none;
  border: none;
  flex: 1 1 auto;
  background-color: none;
  font-size: 0.75rem;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  padding: 0px;
  -webkit-appearance: textfield;
  background: none;

  ::-webkit-search-decoration {
    -webkit-appearance: none;
  }

  [type='number'] {
    -moz-appearance: textfield;
  }

  ::-webkit-outer-spin-button,
  ::-webkit-inner-spin-button {
    -webkit-appearance: none;
  }

  ::placeholder {
    // color: ${({ theme }) => theme.text4};
    color:#DADADA;
  }
`

export const MyBalanceTokenBox = styled.div`
  width: 100%;
  height: 230px;
  overflow-y: hidden;
  overflow-x: auto;
  &.showMore {
    height: auto;
    overflow: auto;
  }
`

export const DBTables = styled.table`
  min-width: 100%;
  table-layer: fixed;
  border-spacing:0px 10px;

`
export const DBTablesDiv = styled.div`
  min-width: 100%;
`
export const DBThead = styled.thead`
  width: 100%;
  border-radius: 0.5625rem;
  box-shadow: 0 0.125rem 0.25rem 0 rgba(0, 0, 0, 0.04);
  background-color: transparent;
  margin-bottom: 0.625rem;
  font-size: 12px;
  tr {
    box-shadow: 0 0.125rem 0.25rem 0 rgba(0, 0, 0, 0.04);
  }
  @media screen and (max-width: 960px) {
    padding: 1rem 5px;
  }
`
export const DBTheadDiv = styled.div`
  width: 100%;
  border-radius: 0.5625rem;
  box-shadow: 0 0.125rem 0.25rem 0 rgba(0, 0, 0, 0.04);
  // background-color: ${({ theme }) => theme.contentBg};
  margin-top: 0.625rem;
  margin-bottom: 0.625rem;
  font-size: 12px;

  @media screen and (max-width: 960px) {
    padding: 1rem 5px;
  }
`
export const DBTheadTrDiv = styled.div`
  ${({ theme }) => theme.flexRowNoWrap};

  box-shadow: 0 0.125rem 0.25rem 0 rgba(0, 0, 0, 0.04);
`

export const DBTh = styled.th`
  color: ${({ theme }) => theme.text4};
  // border: 3px solid rgba(159, 164, 252, 0.50);
  // background-color: ${({ theme }) => theme.contentBg};
  padding: 12px 8px;
  white-space: nowrap;
  font-size: 0.875rem;
  font-weight: bold;
  line-height: 1.5;
  &.r {
    text-align: right;
  }
  &.l {
    text-align: left;
  }
  &.c {
    text-align: center;
  }
  &.hideSmall {

  }
  ${({ theme }) => theme.mediaWidth.upToMedium`
    &.hideSmall {
      display:none;
    }
  `}
`

export const DBThDiv = styled.div`
  color: ${({ theme }) => theme.textColorBold};
  background-color: ${({ theme }) => theme.contentBg};
  padding: 12px 8px;
  white-space: nowrap;
  font-size: 0.875rem;
  font-weight: bold;
  line-height: 1.5;
  &.r {
    text-align: right;
  }
  &.l {
    text-align: left;
  }
  &.c {
    text-align: center;
  }
  &.hideSmall {

  }
  &.token {
    width: 140px;
  }
  &.chain {
    flex: 1;
  }
  &.liquidity {
    width: 150px;
  }
  &.detail {
    width: 110px;
  }
  ${({ theme }) => theme.mediaWidth.upToMedium`
    &.hideSmall {
      display:none;
    }
  `}
`

export const DBTbody = styled.tbody`
  width: 100%;
  border-radius: 0.5625rem;
  border: solid 1px rgba(0, 0, 0, 0.1);
  // background-color: ${({ theme }) => theme.contentBg};
  margin-bottom: 0.625rem;
  font-size: 12px;
  tr {
    // box-shadow: 0 0.125rem 0.25rem 0 rgba(0, 0, 0, 0.04);
    box-shadow: ${({ theme }) => theme.tableShadow};
  }
  @media screen and (max-width: 960px) {
    padding: 1rem 5px;
  }
`

export const DBTbodyDiv = styled.div`
  width: 100%;
  border-radius: 0.5625rem;
  // border: solid 1px rgba(0, 0, 0, 0.1);
  // background-color: ${({ theme }) => theme.contentBg};
  margin-bottom: 0.625rem;
  font-size: 12px;
  @media screen and (max-width: 960px) {
    padding: 1rem 5px;
  }
`

export const DBTbodyTrDiv = styled.div`
  ${({ theme }) => theme.flexRowNoWrap};
  box-shadow: ${({ theme }) => theme.tableShadow};
`

export const DBTd = styled.td`
  background-color: transparent;
  padding: 8px 8px;
  white-space: nowrap;
  font-size: 0.875rem;
  font-weight: bold;
  line-height: 1.5;
  color: ${({ theme }) => theme.textColorBold};
  &.r {
    text-align: right;
  }
  &.l {
    text-align: left;
  }
  &.c {
    text-align: center;
  }
  p {
