import React from 'react'
import { Text } from 'rebass'
import styled from 'styled-components'
// import Logo from '../../assets/svg/logo.svg'
// import LogoDark from '../../assets/svg/logo_white.svg'
import Logo from '../../assets/svg/logo.png'
import LogoDark from '../../assets/images/logo.png'
import LogoColor from '../../assets/images/apple-touch-icon.png'

import { useBaseBalances } from '../../hooks/useAllBalances'
import {useActiveReact} from '../../hooks/useActiveReact'
import { useDarkModeManager, useUserSelectChainId } from '../../state/user/hooks'
import { useNoWalletModalToggle } from '../../state/application/hooks'

// import { ExternalLink } from '../../theme'

// import Row, { RowFixed } from '../Row'
import { RowFixed } from '../Row'
import Web3Status from '../Web3Status'
import SelectNetwork from './SelectNetwork'
import NavListTop from './NavListTop'
// import usePrevious from '../../hooks/usePrevious'
import config from '../../config'
import { thousandBit } from '../../utils/tools/tools'


const HeaderFrameBox = styled.div`
  // position: fixed;
  // top:0;
  // left:0;
  // right:0;
  width:100%;
  background-color: ${({ theme }) => theme.navBg};
  box-shadow: 0px 34px 94px 0px #141251;
`

const HeaderFrame = styled.div`
  display: flex;
  grid-template-columns: 1fr 120px;
  align-items: center;
  justify-content: space-between;
  align-items: center;
  flex-direction: row;
  width: 100%;
  top: 0;
  position: relative;
  background-color: ${({ theme }) => theme.navBg};
  padding: 0rem 1rem;
  z-index: 2;
  height: 70px;
  margin:auto;
  max-width: 1440px;
  ${({ theme }) => theme.mediaWidth.upToMedium`
    grid-template-columns: 1fr;
    padding: 0 1rem;
    width: calc(100%);
    position: relative;
  `};
  ${({ theme }) => theme.mediaWidth.upToExtraSmall`
    padding: 0.5rem 1rem;
  `}
`

const HeaderControls = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-self: flex-end;
  height: 100%;
  padding: 0.875rem 0rem;
  font-family: 'Montserrat', sans-serif;

  ${({ theme }) => theme.mediaWidth.upToMedium`
    flex-direction: row;
    justify-content: flex-end;
    justify-self: center;
    width: 100%;
    max-width: 960px;
    // padding: 1rem;
    padding: 0rem;
    z-index: 99;
    border-radius: 12px 12px 0 0;
    // background-color: ${({ theme }) => theme.bg1};
  `};
`

const HeaderElement = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  font-family: 'Montserrat', sans-serif;

  ${({ theme }) => theme.mediaWidth.upToMedium`
   flex-direction: row-reverse;
    align-items: center;
  `};
`
const HeaderRow = styled(RowFixed)`
  position: relative;
  height: 100%;
  padding: 0.875rem 0rem;
  ${({ theme }) => theme.mediaWidth.upToMedium`
    height: 100%;
    width: 100%;
  `};
`

const AccountElement = styled.div<{ active: boolean }>`
  display: flex;
  flex-direction: row;
  align-items: center;
  background-color: ${({ theme, active }) => (!active ? theme.bg1 : theme.bg3)};
  border-radius: 12px;
  white-space: nowrap;
  width: 100%;
  cursor: pointer;

  :focus {
    border: 1px solid blue;
  }
  /* :hover {
    background-color: ${({ theme, active }) => (!active ? theme.bg2 : theme.bg4)};
  } */
`
const BalanceText = styled(Text)`
  ${({ theme }) => theme.mediaWidth.upToExtraSmall`
    display: none;
  `};
`

const Title = styled.a`
  display: flex;
  align-items: center;
  pointer-events: auto;
  justify-self: flex-start;
  margin-right: 12px;
  height: 100%;
  ${({ theme }) => theme.mediaWidth.upToSmall`
    justify-self: center;
    margin-right: 2px;
  `};
  :hover {
    cursor: pointer;
  }
`

const UniIcon = styled.div`
  ${({ theme }) => theme.flexSC};
  height: 100%;
  img {
    height:42px
  }
  .hiddenImg {
    display: none;
  }
  .viewImg {
    display: block;
  }
  ${({ theme }) => theme.mediaWidth.upToSmall`
    .hiddenImg {
      display: block;
    }
    .viewImg {
      display: none;
    }
    img {
      height:36px
    }
  `};
`

// const VersionLinkBox = styled(ExternalLink)`
//   ${({theme}) => theme.flexSC}
//   text-decoration: none;
//   color: rgb(115, 75, 226);
//   line-height: 26px;
//   margin-top: 17px;
//   font-size: 18px;
//   font-weight:bold;
//   display:none;
// `

function ViewAccountInfo () {
  const {selectNetworkInfo} = useUserSelectChainId()

  const {account, chainId} = useActiveReact()
  const baseBalance = useBaseBalances(account)
  const toggleWalletModal = useNoWalletModalToggle()
// console.log(baseBalance?.toSignificant(3))
  if (selectNetworkInfo?.label === 'NOWALLET') {
    return <AccountElement active={!!account} style={{ pointerEvents: 'auto' }} onClick={toggleWalletModal}>
      <Web3Status />
    </AccountElement>
  }
  return (
    <AccountElement active={!!account} style={{ pointerEvents: 'auto' }}>
      {account && baseBalance ? (
        <BalanceText style={{ flexShrink: 0 }} pl="0.75rem" pr="0.5rem" fontWeight={500}>
          {/* {baseBalance?.toSignificant(6)} {config.getCurChainInfo(chainId).symbol} */}
          {thousandBit(baseBalance?.toExact(), 2)} {config.getCurChainInfo(chainId).symbol}
        </BalanceText>
      ) : null}
      <Web3Status />
    </AccountElement>
  )
}

function MultiLogo () {
  const [isDark] = useDarkModeManager()

  return (
    <UniIcon>
      <img src={isDark ? LogoDark : Logo} alt="logo" className='viewImg' />
      <img src={LogoColor} alt="logo" className='hiddenImg' />
    </UniIcon>
  )
}

export default function Header() {
  // console.log(userEthBalance)
  return (
    <HeaderFrameBox>
      <HeaderFrame>
        <HeaderRow>
          <Title href="/" target="__blank">
            <MultiLogo />
          </Title>
          {/* <VersionLinkBox href='https://v1.anyswap.exchange'>
            V1↗
          </VersionLinkBox> */}
          <NavListTop position='top' />
        </HeaderRow>
        <HeaderControls>
          <HeaderElement>
            <SelectNetwork />
            <ViewAccountInfo />

          </HeaderElement>
        </HeaderControls>
      </HeaderFrame>
    </HeaderFrameBox>
  )
}
